async function fetchStatus(content, type) {
    const stream = content.body.getReader();
    const contentLength = content.headers.get('Content-Length');
    let data = new Uint8Array(contentLength);
    let chunkLength = 0;
    while (chunkLength < contentLength) {
        const {done, value} = await stream.read();
        data.set(value, chunkLength);
        chunkLength += value.length;
        let status = chunkLength < contentLength? chunkLength: 0;
        API.createData('statusProgress', {
            currentStatus: status,
            iterationsNumber: parseInt(contentLength)
        });
    }
    let result;
    const decoder = new TextDecoder("utf-8");
    switch (type) {
        case 'string': 
            result = decoder.decode(data);
            break

        case 'JSON':
            result = JSON.parse(decoder.decode(data));
            break

        case 'dataView':
            result = new DataView(data.buffer)
            break
    }
    return result
}

