function getDensityScaleColor() {
    let points = [];
    for (let i = 0; i < xParameter.length; i++) {
        points[i] = { x: xParameter[i], y: yParameter[i] }
    }

    const tree = new kdTree(
        JSON.parse(JSON.stringify(points)),
        distance, 
        ["x", "y"]
    );

    let nnArray = [];
    for (let i = 0; i < points.length; i++) {
        const nearest = tree.nearest(points[i], 20);
        let counter = 0;
        for (const item of nearest) {
            if (item[1] < 300) counter++;
        }
        nnArray.push(counter)
    }

    const max = getMax(nnArray)
    const min = getMin(nnArray)

    const densityScale = chroma.scale(['blue', 'cyan',  'yellow', 'red'])
        .domain([min, max]).mode('lch');

    return nnArray.map((x, j) => densityScale(x));
}

function distance(a, b){
  return Math.sqrt(Math.pow(a.x - b.x, 2) +  Math.pow(a.y - b.y, 2));
}

