function histogram(array, options={}){
    const {step = 10} = options;
    const max = getMax(array);
    const nbSteps = Math.ceil(max/step)
    let result = [];
    for (let i = 0; i < nbSteps; i++) {
        let counter = 0;
        for (let j = 0; j < array.length; j++) {
            if (i * step < array[j] && array[j] < (i + 1) * step) counter++;
        }
        result.push({
            nbPoints: counter,
            size: i * step
        })
    }
    return result
}

