switch (droppedAnalysis.file.contentType) {
  case 'text/csv': {
    analysis = parseCSV(droppedAnalysis.file.content);
    break;
  }
  case 'text/plain': {
    analysis = parseTXT(droppedAnalysis.file.content);
    break;
  }
}

addToThePlate(analysis);

function addToThePlate(analysis) {
  const values = analysis.map((item) => item.data.value);
  const maxValue = max(values);
  const minValue = min(values);
  for (const result of analysis) {
    wellPlate.getWell({ id: result.well }).addAnalysis(result.data);
  }
  wellPlateSettings.value.max = maxValue;
  wellPlateSettings.value.min = minValue;
  wellPlateSettings.list = Object.keys(wellPlate.wells[0].analysis.raw);
  wellPlate.updateSamples();
}

function parseTXT(content) {
  const contentRows = content.split('\n');
  const rowLabels = [];
  const result = [];
  const blankIndex =
    contentRows.findIndex((item) => item.includes('Blanco sustraído')) + 3;
  const absorbanceIndex =
    contentRows.findIndex((item) => item.includes('Absorbancias')) + 3;
  if (blankIndex) {
    const columnLabels = contentRows[blankIndex - 1]
      .split(/\s+/)
      .filter((item) => item);
    const parsed = [];
    for (let i = blankIndex; i < blankIndex + 8; i++) {
      const row = contentRows[i].split(/\s+/).filter((item) => item);
      rowLabels.push(row.splice(0, 1)[0]);
      parsed.push(row);
    }
    const wells = [];
    for (let i = 0; i < parsed.length; i++) {
      for (let j = 0; j < parsed[0].length; j++) {
        wells.push({
          well: `1-${rowLabels[i]}${columnLabels[j]}`,
          data: {
            name: label + '_BS',
            value: parseFloat(parsed[i][j]),
          },
        });
      }
    }
    return wells;
  } else if (absorbanceIndex) {
    const parsed = [];
    const columnLabels = contentRows[absorbanceIndex - 1]
      .split(/\s+/)
      .filter((item) => item);
    for (let i = absorbanceIndex; i < absorbanceIndex + 8; i++) {
      const row = contentRows[i].split(/\s+/).filter((item) => item);
      rowLabels.push(row.splice(0, 1)[0]);
      parsed.push(row);
    }
    const wells = [];
    for (let i = 0; i < parsed.length; i++) {
      for (let j = 0; j < parsed[0].length; j++) {
        wells.push({
          well: `1-${rowLabels[i]}${columnLabels[j]}`,
          data: {
            name: label + '_Raw',
            value: parseFloat(parsed[i][j]),
          },
        });
      }
    }
    return wells;
  } else {
    UI.showNotification('There is no blank subtracted or raw data', 'error');
  }
}

