function parseCSV(content) {
  const papaparsed = Papa.parse(content, { delimiter: ',', header: false });
  const data = papaparsed.data;
  let initRow, initCol;
  for (let i = 0; i < data.length - 1; i++) {
    if (data[i][0] === 'A') initRow = i;
  }
  for (let i = 0; i < data[initRow - 1].length; i++) {
    if (data[initRow - 1][i] === '1') initCol = i;
  }
  const columnLabels = data[initRow - 1];
  const analysis = [];
  for (let i = initRow; i < data.length - 3 + initRow; i++) {
    const row = [];
    for (let j = initCol; j < data[1].length - 1 + initCol; j++) {
      row[j - initCol] = {
        well: `1-${data[i][0]}${columnLabels[j]}`,
        data: {
          name: label,
          value: parseFloat(data[i][j]),
        },
      };
    }
    analysis.push(...row);
  }
  return [analysis];
}

