function parseSpectra(raw) {
  let xAxis = [];
  for (let j = 3; j < raw[0].length; j++) {
    xAxis.push(raw[1][j]);
  }
  let spectra = [];
  for (let i = 2; i < raw.length; i++) {
    let spectrum = [];
    let label = `${raw[i][0]}${raw[i][1]}`;
    for (let j = 3; j < raw[0].length; j++) {
      spectrum.push(raw[i][j]);
    }
    spectra.push({
      label: label,
      array: { x: xAxis, y: spectrum },
    });
  }
  return spectra;
}

function parseCurves(raw) {
  let data = checkData(raw);
  let xAxis = [];
  for (let j = 1; j < data.length; j++) {
    let time = data[j][0].split(':');
    let seconds =
      parseInt(time[0]) * 3600 + parseInt(time[1]) * 60 + parseInt(time[2]);
    xAxis.push(seconds);
  }

  let curves = [];
  for (let i = 2; i < data[0].length; i++) {
    let curve = [];
    for (let j = 1; j < data.length; j++) {
      curve.push(data[j][i]);
    }
    let label = data[0][i];
    curves.push({
      label: `${label}`,
      array: { x: xAxis, y: curve },
    });
  }
  return curves;
}

function checkData(data) {
  let result = [];
  for (let i = 0; i < data.length; i++) {
    let row = [];
    for (let j = 0; j < data[0].length; j++) {
      if (
        data[i][j] !== '' &&
        data[i][j] != undefined &&
        data[i][j] != null &&
        data[i][j] != NaN
      )
        row.push(data[i][j]);
    }
    if (row.length !== 0 && row.length !== 1) result.push(row);
  }
  return result;
}

