        for (const operation of operationsSettings.operations) {
          const group = getGroup(operation.group);
          let control = getGroup(operation.control);
          if (!group.length) UI.showNotification(`There's no samples assigned to ${operation.group}`, 'error');
          if (!control.length) UI.showNotification(`There's no sample assigned to control ${operation.control}`, 'error');
          if (control.length > 1) {
            let sum = 0;
            for (const datum of control) {
              sum += datum.analysis.raw[analysisLabel[0]]
            }
            control = sum / control.length;
          } else {
            control = control[0].analysis.raw[analysisLabel[0]];
          }

          for (const sample of group) {
            const analysisResult=sample.analysis.raw[analysisLabel[0]];
            const viability = (analysisResult / control) * 100;
            sample.results.normalization = {
              label: analysisLabel[0],
              value: {
                viability,
                mortality: 100 - viability,
              },
            };
          }
        }
      }
    }
  } else if (this.action.name === 'resetOperations') {
    for (const well of wellPlate.wells) {
      const raw = DataObject.resurrect(well.analysis.raw);
      well.analysis.processed = JSON.parse(JSON.stringify(raw));
    }
  }
