function getGroup(groupName) {
  const result = [];
  for (const sample of samples) {
    if (sample.metadata.group === groupName) result.push(sample);
  }
  return result;
}

function getClosestIndex(array, target) {
  const arrayLength = array.length - 1;
  return arrayLength - findClosestIndex(array.slice().reverse(), target);
}

function getConcentrations(max, min, steps) {
  let result = [];
  for (let i = min; i < max; i += (max - min) / steps) {
    result.push(i);
  }
  return result;
}

