function getCurve(growthCurve, color) {
    let curveChart = {};
    let axis = [];
    let replicates = []
    for (let curve of growthCurve) {
        let parsedJcamp = jcampConverter.convert(DataObject.resurrect(curve).jcamp).spectra[0];
        let replicate = { x: [], y: []}
        for (let i=0; i<parsedJcamp.nbPoints; i++) {
            replicate.x.push(parsedJcamp.data[0][i*2]);
            replicate.y.push(parsedJcamp.data[0][i*2+1])
        }
        axis = [
            {label: parsedJcamp.xUnit},
            {label: parsedJcamp.yUnit},
        ]
        replicates.push(replicate);
    };
    let curveAverage = [];
    for (let j = 0; j < replicates[0].x.length; j++) {
        let variable = 0;
        for (let k = 0; k < replicates.length; k++) {
            variable+= replicates[k].y[j]
        }
        curveAverage[j] = variable/replicates.length;
    }

    curveChart.x = replicates[0].x;
    curveChart.y = curveAverage;
    curveChart.xAxis = 0,
    curveChart.yAxis = 1,
    curveChart.styles = {
        selected: {
            lineColor: color,
            lineStyle: 1,
            lineWidth: 2,
        },
        unselected: {
            lineColor: color,
            lineStyle: 1,
            lineWidth: 2,
        }
    };
    return {
        chart: curveChart,
        axis: axis,
        replicates: replicates
    }
};

