console.log('variable', this.variable, 'action', this.action);
const sample = API.cache('sample');
const generalKind = sample? sample.sample.$content.general.kind: undefined;

if (sample) {
    this.getButton('save').setColor('green');
    this.enableButton('save');
} else {
    this.getButton('save').setColor('grey');
    this.disableButton('save');
}

if (generalKind === 'fraction') {
    this.enableButton('images');
} else {
    this.disableButton('images');
}

const layers={
    'default': {
        layer: 'Default layer'
    },
    'images': {
        layer: 'images'
    }
}

if (layers[this.button]) {
    for (let key of Object.keys(layers)) {
        if (key === 'images' && generalKind !== 'fraction') continue;
        this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
} else {
    switch (this.button) {
        case 'save':
            API.doAction('save');
            break;
    }
}

console.log('this.action: ', this.action)
const roc = API.cache('roc')
const sample=API.cache('sample');
const newSample = JSON.parse(JSON.stringify(sample.sample.resurrect()));
const entrysample = getJSONTemplate(newSample);
const sampleBatch = builtSampleBatch();
const sampleReference = newSample.$id[0];
const kind = getParentKind(newSample);

if (!sample) return;
if(this.action) {
    switch (this.action.name) {
        case 'updateSamples': {
            const targetReference = API.cache('targetReference');
            roc.query('fraction_toc').then(async function(data) {
                data = data.filter((item) => item.value.reference.split(" ")[0] === targetReference)
                await API.createData('fractionSamplesFiltered', data);
                console.log('updated samples')
            })
            break;
        }
        case 'save': {
            if (kind == "extract") {
                await createSample(entrysample, 'fraction', sampleBatch, sampleReference);
            } else {
                sample.sample.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
                sample.handleAction(this.action);
            }
            break;
        }
        default: {
            sample.sample.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
            sample.handleAction(this.action);
            break;    
        }
    }
} else if (this.variable) {
    switch (this.variable) {
        case 'droppedData':
            sample.handleDrop(this.variable, true, {
                autoJcamp: true
            });
            break;
        case 'droppedVideo':
        case 'droppedImage':
        case 'droppedOther':
            console.log(this.variable)
            await sample.handleDrop(this.variable);
            break;
        case 'droppedOverview':
            sample.handleOverview(this.variable);
            break;
    }
}

async function createSample(entrysample, kind, sampleBatch, sampleReference) {
    UI.confirm(`<div> Are you sure to save: ${sampleBatch}? </div>`)
    .then(function(result){
        if (!result) return;
        entrysample.$id = [sampleReference, sampleBatch];
        entrysample.$content.general.kind = kind;
        if (result.sample) entrysample.$id.push(result.sample);
        let reference=sampleReference + ( sampleBatch ? ' '+sampleBatch : '');
        let userInfo = API.cache('userInfo');
        if (userInfo) {
            entrysample.$owners=[userInfo.email];
        }
        roc.create(entrysample)
            .then(() => { API.doAction('updateSamples') });
    });
}

function getJSONTemplate(parentSample){
    const result = {
        "$type": "entry",
        "$id": [
            "reference",
            "batchID"
        ],
        "$kind": "sample",
        "$owners": [
            "admin@cheminfo.org"
        ],
        "$content": "",
        "$lastModification": "admin@cheminfo.org",
        "$modificationDate": "",
        "$creationDate": ""
    };
    result.$content = parentSample.$content;
    result.$content.image = [];
    result.$content.spectra = {};
    result.$content.general.metadata.parent.extract = getParent(parentSample);
    result.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
    return result;
}

function getParent(sample) {
    return {
        reference: sample.$id[0],
        batch: sample.$id[1],
        uuid: sample._id,
    }
}

function builtSampleBatch() {
    return `${API.getData('sampleIdentifier').resurrect()}`;
}

function getParentKind(parentSample) {
    return parentSample.$content.general.kind;
}

