script:
const roc = API.cache('roc')
const sample=API.cache('sample');
const newSample = JSON.parse(JSON.stringify(sample.sample.resurrect()));
const entrysample = getJSONTemplate(newSample);
const sampleBatch = builtSampleBatch(newSample);
const sampleReference = newSample.$id[0];
const kind = getParentKind(newSample);

if (!sample) return;
if(this.action) {
    switch (this.action.name) {
        case 'updateSamples': {
            const targetReference = API.cache('targetReference');
            roc.query('soil_toc').then(async function(data) {
                data = data.filter((item) => item.value.reference.split(" ")[0] === targetReference)
                await API.createData('soilSamplesFiltered', data)
            })
            break;
        }
        case 'save': {
            if (kind == "agro") {
                await createSample(entrysample, 'soil', sampleBatch, sampleReference);
            } else {
                sample.sample.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
                sample.handleAction(this.action);
            }
            break;
        }
        default: {
            sample.sample.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
            sample.handleAction(this.action);
            break;    
        }
    }
} else if (this.variable) {
    switch (this.variable) {
        case 'droppedData':
            sample.handleDrop(this.variable, true, {
                autoJcamp: true
            });
            break;
        case 'droppedVideo':
        case 'droppedImage':
        case 'droppedOther':
            console.log(this.variable)
            await sample.handleDrop(this.variable);
            break;
        case 'droppedOverview':
            sample.handleOverview(this.variable);
            break;
    }
}

async function createSample(entrysample, kind, sampleBatch, sampleReference) {
    UI.confirm(`<div> Are you sure to save: ${sampleBatch}? </div>`)
    .then(function(result){
        if (!result) return;
        entrysample.$id = [sampleReference, sampleBatch];
        entrysample.$content.general.kind = kind;
        if (result.sample) entrysample.$id.push(result.sample);
        let reference=sampleReference + ( sampleBatch ? ' '+sampleBatch : '');
        let userInfo = API.cache('userInfo');
        if (userInfo) {
            entrysample.$owners=[userInfo.email];
        }
        roc.create(entrysample);
        API.doAction('updateSamples')
    });
}

function getJSONTemplate(parentSample){
    const result = {
        "$type": "entry",
        "$id": [
            "reference",
            "batchID"
        ],
        "$kind": "sample",
        "$owners": [
            "admin@cheminfo.org"
        ],
        "$content": "",
        "$lastModification": "admin@cheminfo.org",
        "$modificationDate": "",
        "$creationDate": ""
    };
    result.$content = parentSample.$content;
    result.$content.image = [];
    result.$content.spectra = {};
    result.$content.general.metadata.parent = getParent(parentSample);
    result.$content.general.metadata.sampleIdentifier = API.getData('sampleIdentifier').resurrect();
    return result;
}

function getParent(sample) {
    return {
        reference: sample.$id[0],
        batch: sample.$id[1],
        uuid: sample._id,
    }
}

function builtSampleBatch(parentSample) {
    const sampleIdentifier = API.getData('sampleIdentifier').resurrect();
    return `${sampleIdentifier}`;
}

function getParentKind(parentSample) {
    return parentSample.$content.general.kind;
}

this.getButton('save').setColor('green');
this.disableButton('images');
const sample = API.cache('sample');
const generalKind = sample.sample.$content.general.kind;

if (sample) {
    this.getButton('save').setColor('green');
    this.enableButton('save');
} else {
    this.getButton('save').setColor('grey');
    this.disableButton('save');
}

if (generalKind === 'soil') {
    this.enableButton('images');
} else {
    this.disableButton('images');
}

const layers={
    'default': {
        layer: 'Default layer'
    },
    'soil': {
        layer: 'soil'
    },
    'images': {
        layer: 'images'
    }
}

if (layers[this.button]) {
    for (let key of Object.keys(layers)) {
        if (key === 'images' && generalKind !== 'soil') continue;
        this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
} else {
    switch (this.button) {
        case 'save':
            API.doAction('save');
            break;
    }
}

const selectedSample = API.getData('selectedSample').resurrect();
const data = API.cache('data');

const sample = new Sample(data.couchDB, selectedSample.id, {
    track:false,
    onSync(synced) {
        console.log('received synced event', synced);
        API.createData('dataSynced', synced);
    }
});

sample.waitSampleLoaded().then(() => {
    loadSampleFeatures(sample, 'soil');
    API.doAction('rowActivated');
});

API.cache('sample', sample);
API.doAction('dataSynced', '');

function loadSampleFeatures(sample, viewKind) {
    const content = sample.sample.$content;
    const sampleKind = getParentKind(sample);
    const sampleIdentifier = sampleKind == viewKind ?
        content.general.metadata.sampleIdentifier:
        'This field must be replace it';
    API.createData('content', content);
    API.createData('sampleIdentifier', sampleIdentifier);
    API.createData('sampleInfo', {
        reference: sample.sample.$id[0],
        batch: sample.sample.$id[1],
        generalKind: sample.sample.$content.general.kind,
        sampleIdentifier: sample.sample.$content.general.metadata.sampleIdentifier,
        parent: sample.sample.$content.general.metadata.parent,
    })
    API.doAction('sampleSynced');
    UI.showNotification('Updated current sample', 'success');
}

function getParentKind(parentSample) {
    return parentSample.sample.$content.general.kind;
}

const [
    Renderer,
    printerFactory,
    printerHelper,
    Toc,
    elnPlugin,
    Sample,
    loadTwig,
] = await API.require([
    'vh/eln/acs/renderer',
    'vh/eln/stock/printer/factory',
    'vh/eln/stock/printer/helper',
    'vh/eln/Toc',
    'https://www.lactame.com/lib/eln-plugin/0.0.2/eln-plugin.js',
    'vh/eln/Sample',
    'vh/eln/templates/loadTwig',
]);

Renderer.add();

printerFactory(async function(printer) {
    printerHelper.setup(printer, ['sample', 'location']);
});

let sample
if (IframeBridge) {
    IframeBridge.onMessage(onMessage);
    IframeBridge.ready();
    async function onMessage(data) {
        if(data.type === 'tab.data') {
            console.log(data)
            const sample = new Sample(data.message.couchDB, data.message.uuid, {
                track: false,
                onSync(synced) {
                    console.log('received synced event', synced);
                    API.createData('dataSynced', synced);
                }
            });

            sample.waitSampleLoaded().then((result) => {
                let content = sample.sample.$content;
                API.createData('content', content);
                API.createData('sampleIdentifier', 'This field must be replace it');
                API.createData('sampleInfo', {
                    reference: sample.sample.$id[0],
                    batch: sample.sample.$id[1],
                    generalKind: sample.sample.$content.general.kind,
                    sampleIdentifier: sample.sample.$content.general.metadata.sampleIdentifier,
                    parent: sample.sample.$content.general.metadata.parent,
                })
            });
            API.cache('couchDB', data.message.couchDB);
            API.cache('data', data.message);
            API.cache('sample', sample);
            const roc = API.cache('roc');
            roc.getGroupMembership().then(groups => API.cache('allGroups', groups));
            roc.getUserInfo().then( (userInfo) => {
                userInfo.username = userInfo.username || userInfo.userId || userInfo.email;
                API.createData('userInfo', userInfo);              
            });
            const targetReference = DataObject.resurrect(data.message.reference.split(" ")[0]);
            API.cache('targetReference', targetReference)
            roc.query('soil_toc').then(function(data) {
                data = data.filter((item) => item.value.reference.split(" ")[0] === targetReference)
                API.createData('soilSamplesFiltered', data);
            })
            var layer=window.localStorage.getItem('defaultSampleLayer') || 'molecule';
            API.createData('currentLayer', layer);
        }
    }
}



// Load custom font
require(['src/util/util'], function(Util) {
    Util.loadCss('https://www.lactame.com/lib/cheminfo-font/0.7.2/style.css');
})

return {
    filter: function(value, resolve) {
        var roc = API.cache('rocReaction');
        var v = value.get();
        if(roc) {
            roc.typeUrl(v.$content, v);    
        }
        return resolve(value)
    }
