const roc = API.cache('roc')
const sample=API.cache('sample');
const gatManager = API.cache('gatManager');
const newSample = JSON.parse(JSON.stringify(sample.sample.resurrect()));
console.log(newSample)
const entrysample = gatManager.getJSONTemplate(newSample);
console.log(entrysample)
const sampleBatch = gatManager.builtSampleBatch(newSample);
const sampleReference = newSample.$id[0];
const kind = gatManager.getParentKind(newSample);

if (!sample) return;
if(this.action) {
    switch (this.action.name) {
        case 'updateSamples': {
            gatManager.getToc('vegetal_toc', 'vegetalSamplesFiltered')
            break;
        }
        case 'save': {
            if (kind == "agro") {
                await gatManager.createSample(entrysample, 'vegetal', sampleBatch, sampleReference);
            } else {
                sample.handleAction(this.action);
            }
            break;
        }
        default: {
            sample.handleAction(this.action);
            break;    
        }
    }
} else if (this.variable) {
    switch (this.variable) {
        case 'droppedData':
            sample.handleDrop(this.variable, true, {
                autoJcamp: true
            });
            break;
        case 'droppedVideo':
        case 'droppedImage':
        case 'droppedOther':
            console.log(this.variable)
            await sample.handleDrop(this.variable);
            break;
        case 'droppedOverview':
            sample.handleOverview(this.variable);
            break;
    }
}

