function resurrect(object){
    let keys = Object.keys(object);
    for (let key of keys) {
        object[key] = DataObject.resurrect(object[key]);
    }
}

const aMax = ML.Array.max;
const aMin = ML.Array.min;
const aMean = ML.Array.mean;
const findClosestIndex = SP.xFindClosestIndex;
const wellPlate = API.cache('wellPlate');
if (!wellPlate) return;
wellPlate.resurrect();
const samples = wellPlate.samples;
const operationsSettings = API.getData('operationsSettings').resurrect();
if (!operationsSettings.displayGroup) return;
const groups = getGroup(operationsSettings.displayGroup);
const wellPlateSettings = API.getData('wellPlateSettings').resurrect();
const xAxis = [];
const units = [];
for (let sample of groups) {
  const well = wellPlate.getWell({ id: sample.wells[0].id });
  const reagents = well.reagents.filter(
    (item) => item.label === operationsSettings.selectedReagent,
  )[0];
  const concentration = reagents.concentration;
  units.push(reagents.unit);
  xAxis.push(Math.log10(reagents.concentration));
}

const maxValue = aMax(xAxis);
const minValue = aMin(xAxis);
const viability = groups.map(
  (item) => item.results.normalization.value.viability,
);
const mortality = groups.map(
  (item) => item.results.normalization.value.mortality,
);

const resultTable = [];
for (let i = 0; i < viability.length; i++) {
  resultTable.push({
    x: xAxis[i],
    unit: units[i],
    viability: viability[i],
    mortality: mortality[i],
  });
}

API.createData('resultTable', resultTable);

const xyRegression = new PolynomialRegression(
  xAxis,
  viability,
  operationsSettings.grade,
);

const concentrationArray = getConcentrations(maxValue, minValue, 1000);
const predictedViability = [];
for (const concentration of concentrationArray) {
  predictedViability.push(xyRegression.predict(concentration));
}

const fittedViability = [];
for (const point of xAxis) {
  const pointIndex = findClosestIndex(concentrationArray, point);
  fittedViability.push(predictedViability[pointIndex]);
}

const viabilityMean = aMean(viability);
let sumOfSquaresResidual = 0;
let sumOfSquaresTotal = 0;
for (let i = 0; i < viability.length; i++) {
  sumOfSquaresTotal += Math.pow(viability[i] - viabilityMean, 2);
  sumOfSquaresResidual += Math.pow(viability[i] - fittedViability[i], 2);
}

const r2 = 1 - sumOfSquaresResidual / sumOfSquaresTotal;
const log10CIndex = getClosestIndex(predictedViability, 50);
const log10C = concentrationArray[log10CIndex];
const heigh = Math.round(aMax(predictedViability));

API.createData('grid', {
