      data: {
        x: xAxis,
        y: new Array(xAxis.length).fill(50),
      },
    },
    {
      data: {
        y: new Array(heigh).fill().map((value, index) => index),
        x: new Array(heigh).fill(log10C),
      },
    },
  ],
});

API.createData('ic50', { ic50: 10 ** log10C, log10C, unit: units[0], r2 });

