template:
{% if legend2d.nucleus %}
  Nucleus: {{ legend2d.nucleus }} <br>
{% endif %}
{% if legend2d.red %}
  <span style="color: red">———</span> {{ legend2d.red }} <br>
{% endif %}
{% if legend2d.green %}
  <span style="color: lightgreen">———</span> {{ legend2d.green }} <br>
{% endif %}
{% if legend2d.blue %}
  <span style="color: blue">———</span> {{ legend2d.blue }}
{% endif %}

script:
const roc=API.cache('roc');

let selected=API.getData('selected');

let data=[];
API.doAction('setTotal', selected.length);
API.doAction('setCurrent', 0);
for (let item of selected) {
    if (item.document.datum) {
        data.push(item.document.datum);
    } else {
        let datum=await roc.get(item.id);
        data.push(datum);
        item.document.datum=datum;
    }
    API.doAction('increment',1);
}
console.log(selected);

API.createData('data', data);
console.log(data);

sendToHome(this.action.value.id);
function sendToHome(id) {
    IframeBridge.postMessage('tab.message', {
        id: 'Home',
        message: {
            action: 'selectEntry',
            uuid: String(id)
        }
    });
    IframeBridge.postMessage('tab.focus', 'Home');
}

var Renderer = await API.require('vh/eln/acs/renderer');
Renderer.add();

require(['vh/rest-on-couch/Roc'], function(Roc) {
    if((typeof IframeBridge)!=='undefined') {
        IframeBridge.onMessage(onMessage);
        IframeBridge.ready();
    }

    async function onMessage(data) {
        if(data.type === 'tab.data') {
            var couchDB = data.message.couchDB;
            var uuid = data.message.uuid;
            API.cache('couchDB', couchDB);
            API.cache('uuid', uuid);
            var roc = new Roc(couchDB);
            API.cache('roc', roc);

            const SampleToc=await API.require('vh/eln/sample/SampleToc');
            const sampleToc = new SampleToc(roc, {

            });
            API.cache('sampleToc', sampleToc);
            sampleToc.initializeGroupForm();

            const loadTemplates = await API.require('vh/eln/templates/load');
            let templates=await loadTemplates(['org.cheminfo.samples.chemical']);
            console.log(templates);


        }
    }
});

