async function generateMasks(image) {
    const masks=[];
    for (let i=0.1; i<1; i+=0.1) {
        var mask=image.mask({algorithm:'threshold', threshold: i});
        API.loading('mask', 'Calculating threshold: '+i);
        await delay(0);
        masks.push(
            {
                name: "threshold: "+i.toPrecision(1),
                image: {
                    type:'png',
                    value: mask.toDataURL('image/png', {useCanvas:true})
                }
            }
        )
    }
    for (let algorithm of Object.keys(IJS.Static.threshold)) {
        var mask=image.mask({algorithm});
        API.loading('mask', 'Calculating mask using: '+algorithm);
        await delay(0);
        masks.push(
            {
                name: algorithm,
                image: {
                    type:'png',
                    value: mask.toDataURL('image/png', {useCanvas:true})
                }
            }
        )
    }
    API.stopLoading('mask');
    return masks;
}

