function createGrey(greyAlgorithm) {
    const original=API.cache('original');

    const grey=original.grey({algorithm: greyAlgorithm, allowGrey:true});
    API.cache('grey', grey);

    API.createData('grey', {
        type:'png',
        value:grey.toDataURL('image/png', {useCanvas:true})
    })
}


function createROI(roiOptions, paintingOptions) {
    var mask=API.cache('mask');
    var grey=API.cache('grey');
    var original=API.cache('original');
    var gaussian=API.cache('gaussian');

    var roiManager = gaussian.getRoiManager();
    API.cache('roiManager',roiManager);
    var algorithm = roiOptions.algorithm;

    switch (roiOptions.algorithm.toLowerCase()) {
        case 'negative':
            roiOptions.negative=true;
            roiOptions.positive=false;
            roiManager.fromMask(mask);
            break;
        case 'positive':
            roiOptions.negative=false;
            roiOptions.positive=true;
            roiManager.fromMask(mask);
            break;
        case 'watershed':
            waterShed(roiManager, gaussian, false);
            break;
        case 'invertwatershed':
            waterShed(roiManager, gaussian, true);
            break;
    }

    function waterShed(roiManager, gaussian, invert) {
        var waterShedOptions={
            closePoints: 2,
            maxEquals: 3
        }

        var points = gaussian.getLocalMaxima({
            invert: ! invert,
            mask:mask,
            region:3,
            maxEquals: waterShedOptions.maxEquals,
            removeClosePoints:waterShedOptions.closePoints
        });

        API.createData('paintedMask',
            mask.rgba8().paintPoints(points).toDataURL('image/png', {useCanvas:true})
        )

        roiManager.fromWaterShed({
            mask:mask,
            points: points
        }); 

        var rois=roiManager.getRois(roiOptions);
        roiManager.mergeRoi({
             algorithm:'commonBorderRatio', // or commonBorderLength
             minCommonBordeRatio: 0.8
         })

    }
    var rois=roiManager.getRois(roiOptions);
    rois = improveROI(original, rois);
    API.cache('rois', rois);
    API.createData('rois', rois);

    API.doAction('UpdateSize');

    var options=JSON.parse(JSON.stringify(Object.assign({}, roiOptions, paintingOptions)));
    if (! options.color)  {
        options.distinctColors=true;
    }

    const pixelSize = API.getData('pixelSize');
    console.log(pixelSize)
    if (pixelSize && pixelSize.SI) {
        options.pixelSize = Number(pixelSize.SI);
        options.unit = String(pixelSize.unit);
    }

    var painted=roiManager.paint(options);

    return API.createData('painted', painted.toDataURL('image/png', {useCanvas:true})).then(function() {
        setHighlight(
            roiManager.getRoiIds(roiOptions),
            roiManager.getMap().data
        );
    });
}

function improveROI(image, rois) {
    let newRois = JSON.parse(JSON.stringify(rois));

    rois.forEach( (roi, index) => {
        // We add small extract of the images ... this could take a lot of memory ...
        const roiMask=roi.getMask({fill:false, scale:preferences.roi.scale});
        const roiImage=image.extract(roiMask);
        newRois[index].image=roiImage.toDataURL('image/png', {useCanvas:true});
        newRois[index]._highlight=roi.id;
    });
    return newRois;
}

function setHighlight(highlight, pixels) {
    var images=[
        API.getData('grey'),
        API.getData('gaussian'),
        API.getData('mask'),
        API.getData('painted'),
    ];

    images.forEach(function(image) {
        image._highlight=highlight;
        image._highlightArray=pixels;
        image.triggerChange();
    })
}


function createGaussian(radius) {
    const grey=API.cache('grey');
    let gaussian = radius ?
        grey.gaussianFilter({radius}).colorDepth(8) :
        grey.colorDepth(8);

    if (preferences.sobelFilter) {
        gaussian = gaussian.sobelFilter();
    }

    API.cache('gaussian', gaussian);
    return API.createData('gaussian', {
        type:'png',
        value:gaussian.toDataURL('image/png', {useCanvas:true})
    });
}

function createMask(algorithm, threshold) {
    var gaussian=API.cache('gaussian');
    var mask=gaussian.mask({
        algorithm: algorithm || 'threshold',
        threshold: threshold
    })

    API.cache('mask', mask);
    return API.createData('mask', {
        type:'png',
        value:mask.toDataURL('image/png', {useCanvas:true})
    });
}

function checkUnit(preferences) {
    if (preferences.roi.filterUnit==="pixel") return preferences;
    let pixelSize=API.getData('pixelSize');
    if (! pixelSize) {
        UI.showNotification('Undefined pixelSize can not filter by unit');
        return preferences;
    }
    pixelSize = pixelSize.resurrect();
    if (! pixelSize.unit || ! pixelSize.SI) {
        UI.showNotification('Undefined pixelSize can not filter by unit');
        return preferences;
    }
    // need to convert to pixels
    let unitDistance = mathjs.unit(pixelSize.unit);
    unitDistance.value = pixelSize.SI;
    let factorDistance = 1 / unitDistance.toNumber(pixelSize.unit);

    let unitSurface = mathjs.unit(pixelSize.unit+'^2');
    unitSurface.value = pixelSize.SI * pixelSize.SI;
    let factorSurface = 1 / unitSurface.toNumber(pixelSize.unit+'^2');


    console.log(factorDistance, factorSurface)

    console.log(JSON.stringify(preferences.roi));
    console.log(preferences.roi.minHeight)
    console.log(preferences.roi.minSurface)
    preferences.roi.minHeight*=factorDistance;
    preferences.roi.maxHeight*=factorDistance;
    preferences.roi.minWidth*=factorDistance;
    preferences.roi.maxWidth*=factorDistance;
    preferences.roi.minSurface*=factorSurface;
    preferences.roi.maxSurface*=factorSurface;
   console.log(preferences.roi.minHeight)
    return preferences;
}

