function generateFilter(category, criteriaLabels) {
    var filter='';
    for (var key of Object.keys(criteriaLabels)) {
        var value=category['min'+key];
        if (! isNaN(parseFloat(value))) {
            filter+='if (roi["'+criteriaLabels[key]+'"]<'+parseFloat(value)+') return false;';
        }
        var value=category['max'+key];
        if (! isNaN(parseFloat(value))) {
            filter+='if (roi["'+criteriaLabels[key]+'"]>'+parseFloat(value)+') return false;';
        }
    }
    filter+='return true;';
    return Function('roi', filter);
}

function createStats(rois) {
    var nbSlots=10;
    var surfaces=rois.map(a=>Number(a.surface*pixelSizeNm*pixelSizeNm)).sort((a,b)=>a-b);
    var step=(surfaces[surfaces.length-1]-surfaces[0])/(nbSlots+1);
    var currentMin=surfaces[0];
    var currentMax=surfaces[0]+step;
    var chartAnnotations=[];
    var count=0;
    for (var i=0; i<surfaces.length; i++) {
        if (surfaces[i]<=currentMax) {
            count++;
        }
        if (surfaces[i]>currentMax || i===(surfaces.length-1)) {
            currentMin=currentMax;
            currentMax+=step;
            chartAnnotations.push(addAnnotation(currentMin, 0, currentMax, count));
            count=0;
        }
    }
    return chartAnnotations;
}

