function arrayToTable(array) {
    let keys=getKeys(array);
    var result=[];
    result.push(keys.join('\t'));
    for (var element of array) {
        var line=[];
        for (var key of keys) {
            let value=element[key];
            if (value && value.exportValue) value=value.exportValue;
            line.push(value);
        }
        result.push(line.join('\t'));
    }
    return result.join('\r\n');
}

function getKeys(array) {
    var keys = {};
    let element = array[0];
    for (var key of Object.keys(element)) {
        if (key === '_highlight') continue;
        let type = typeof element[key];
        if (type==='number' || type==='text' || type==='boolean' || (type==='object' && element[key].SI)) {
            keys[key]=true;
        }
    }
    return Object.keys(keys);
}

