let toHighlight=API.getData('selectedAnnotations');

for (let annotation of annotations) {
    let highlight = (Array.isArray(toHighlight) && toHighlight.filter(highlight => JSON.stringify(highlight.options)===JSON.stringify(annotation.options)).length>0) ?
        { stroke: 'yellow'} : {};

    let style=preferences.annotations.style[annotation.kind];
    if (! style) continue;
    svgCreator.add(
        annotation.kind,
        {...annotation.options, ...style, ...highlight},
    )

    let label='';
    switch (preferences.annotations.label) {
        case 'size':
            let size = annotation.length || annotation.surface;
            if (size) {
                let sizeUnit = mathjs.unit(String(size.unit));
                sizeUnit.value = Number(size.SI);
                label = sizeUnit.toNumber().toPrecision(4);
            }
            break;
        case 'label':
            label = annotation.label
            break;
    }

    if (label) {
        let position = getAnnotationPosition(annotation);
        if (! position) continue;
        svgCreator.addText(
            label,
            {
                x: position.x,
                y: position.y,
                ...textStyle,
                dominantBaseline: 'middle',
                textAnchor: 'middle',
            }
        )
    }
}

API.createData('annotationsSVG',svgCreator.toSVG().replace(/>/g, '>\n'));

function getAnnotationPosition(annotation) {
    let options = annotation.options;
    switch(annotation.kind) {
        case 'line':
            return {
                x: (options.x1 + options.x2) /2,
                y: (options.y1 + options.y2) /2,
            }
            break;
        case 'polygon':
            const points=options.points.split(/[ ,]+/).map(value => Number(value));
            const xs = points.filter( (value, index) => index%2===0);
            const ys = points.filter( (value, index) => index%2===1);
            return {
                x: (Math.max(...xs)+Math.min(...xs)) /2,
                y: (Math.max(...ys)+Math.min(...ys)) /2,
            }
            break;
    }
}

