function getSamples(samples){
    let wells = [];
    for (let selectedSample of selectedSamples) {
        const content = JSON.parse(JSON.stringify(selectedSample.sample.sample.$content));
        let sample = {
            ...content.biology.cellLine.growthCondition
        }
        let spectrum = content.spectra.uv;
        sample.ids = selectedSample.wells.split(', ');
        sample.metadata = content.metadata;
        for (let i = 0; i < sample.ids.length; i++) {
            const currentSpectrum = spectrum.filter((item) => item.id === sample.ids[i])[0];
            const currentCurve = sample.growthCurve.filter((item) => item.id === sample.ids[i])[0];
            const currentResult = sample.analysis.wells.filter((item) => item.id === sample.ids[i])[0];
            if (currentSpectrum && currentSpectrum.id) delete currentSpectrum.id;
            if (currentCurve && currentCurve.id) delete currentCurve.id;
            if (currentResult && currentResult.id) delete currentResult.id;
            wells.push({
                id: sample.ids[i],
                label: sample.ids[i].split('-')[1],
                plate: sample.ids[i].split('-')[0],
                metadata: sample.metadata,
                reagents: sample.media,
                results: currentResult,
                growthCurve: currentCurve,
                spectrum: currentSpectrum,
            })
        }

    }
    return wells;
}

