const preferences = API.getData('preferences').resurrect();
let wellsTemplate = API.getData('wellsTemplate');
let setup = {
    color: false,
    colorOptions: [],
    colorjPath: [],
    colnumber: [preferences.plateSetup.colNumber],
    rownumber: [preferences.plateSetup.rowNumber],
    plateIndex: [preferences.plateSetup.plateNumber],
    direction: [preferences.direction],
    shape: [preferences.plateSetup.shape],
    wellSize: [preferences.plateSetup.wellSize],
}

if (this.action) {
    let parameter = this.action.name;
    switch (this.action.name) {
        case "colorBy": {
            console.log(this.action)
            setup.color = true;
            setup.colorOptions = ["colorByJpath"];
            setup.colorjPath = [`element.experiment[${this.action.value}].concentration`];
            break;
        }

        case "colorByTemplate": {
            console.log(this.action)
            setup.color = true;
            setup.colorOptions = ["colorByJpath"];
            setup.colorjPath = [`element.experiment[${this.action.value}].concentration`];
            API.createData('setupTemplate', setup);
            break;
        } 

        case "colorByExperiment": {
            console.log(this.action)
            setup.color = true;
            setup.colorOptions = ["colorByJpath"];
            setup.colorjPath = [`element.experiment[${this.action.value}].concentration`];
            API.createData('setupExperiment', setup);
            break;
        }

        case "colorByJpathValue": {
            if (!API.getData('colorByJpathValue')) return;
            let colorByJpathValue = API.getData('colorByJpathValue').resurrect();
            setup.color = true;
            setup.colorOptions = ["colorByJpathValue"];
            setup.colorjPath = [];
            setup.jpathValue = [`element.result.${colorByJpathValue.results}.value`];
            setup.max = [colorByJpathValue.max];
            setup.min = [colorByJpathValue.min];
            break
        }

        case "removeColors": {
            console.log('removeColors')
            setup.color = true;
            setup.colorOptions = [];
            setup.colorjPath = [];
            API.createData('setupExperiment', setup);
            API.createData('setupTemplate', setup);
            break;
        }
    }
}

if (!wellsTemplate) return;

if (!API.getData('growthCurves')) return;
const growthCurves = API.getData('growthCurves').resurrect();
const wellsExperiment = API.getData('wellsExperiment');
const analysis = Papa.parse(growthCurves.file.content,
    {
        delimiter:",",
        dynamicTyping:true
    }
);

let curves = [];

let xAxis = analysis.data.filter((item) => item[2].split(" ")[0] == 'Wavelength')
let ordenateX = {
    label: xAxis[0][2],
    curve: JSON.parse(JSON.stringify(xAxis[0])).splice(3, 46)
}

for (let i = 2; i < analysis.data.length; i++) {
    curves.push({
        label: analysis.data[i][0].concat(analysis.data[i][1]),
        sample: analysis.data[i][2],
        curve: JSON.parse(JSON.stringify(analysis.data[i])).splice(3, 46)
    })
};

let sampleWells = [];
for (let i = 0; i < wellsExperiment.length; i++) {
    let item = JSON.stringify(wellsExperiment[i].experiment);
    let feature = sampleWells
    .find((element) => element === item);
    if (feature+1) continue
    sampleWells.push(item)
}

let selected = {
    lineColor: "rgb(0,0,255)",
    lineStyle: 1,
    lineWidth: 2,
};

for (let i = 0; i < wellsExperiment.length; i++) {
    const index = sampleWells.findIndex((item) =>
        JSON.stringify(wellsExperiment[i].experiment) == item);
    let position = wellsExperiment[i].position;
    let curve = curves.filter((element) => element.label === position);
    if (curve.length === 0) {
        wellsExperiment[i].growthCurve = [];
        wellsExperiment[i].info = [];
        continue
    }
    const replicateID = JSON.stringify(DataObject.resurrect(wellsExperiment[i].experiment));
    const replicatesLabels = wellsExperiment
        .filter((item) => JSON.stringify(DataObject.resurrect(item.experiment)) == replicateID)
        .map((item) => item.position);
    wellsExperiment[i].growthCurve = {
        x: ordenateX.curve,
        y: curve[0].curve,
        styles: {
            selected: selected,
            unselected: selected
        }
    }
    wellsExperiment[i].info = {
        position: position,
        inAverage: true,
        color: '#98F274',
        labels: replicatesLabels,
    }
}

wellsExperiment.triggerChange();

let colors = Color.getDistinctColors(wellsExperiment.length);

let samples = [];
for (let i = 0; i < sampleWells.length; i++) {
    let replicates = wellsExperiment.filter((item) => 
        JSON.stringify(DataObject.resurrect(item.experiment)) === DataObject.resurrect(sampleWells[i]));
    let label = replicates.map((well) => well.position)
    let results = replicates.map((replicate) => replicate.result);
    let parameters = replicates[0].experiment;
    let highlight = replicates[0]._highlight;

    replicates = replicates.filter((element) =>
        element = element.growthCurve == 0 ? undefined : element.growthCurve);
    if (replicates.length === 0) continue
    let sample = average(replicates);
    replicates.map((item) => item.position)
    label = replicates.map((x) => x.position).join("-");
    const intensity = Math.max(...sample);
    const maxIndex = sample.findIndex((item) => item == intensity);
    const wavelength = ordenateX.curve[maxIndex];
    samples.push({
        label: label,
        growthCurve: {
            x: ordenateX.curve,
            y: sample,
            styles: {
                selected: {
                    lineColor: `rgb(${colors[i]})`,
                    lineStyle: 1,
                    lineWidth: 2,
                },
                unselected: {
                    lineColor: `rgb(${colors[i]})`,
                    lineStyle: 1,
                    lineWidth: 2,
                }
            },
        },
        max: {
            intensity: intensity,
            wavelength: wavelength,
            index: maxIndex,
        },
        parameters: parameters,
        replicates: replicates,
        _highlight: highlight,
    })

};

API.createData('replicatesAverage', samples);

function average(replicates) {
    let averaged = [];
    const items = replicates.filter((item) =>
        item.info.inAverage == true).length;
    for (let j = 0; j < replicates[0].growthCurve.x.length; j++) {
        let variable = 0;
        for (let k = 0; k < replicates.length; k++) {
            if (replicates[k].info.inAverage == false ||
                !replicates[k].info) continue
            variable+= replicates[k].growthCurve.y[j]
        }
        averaged[j] = variable/items;
    }
    return averaged;
}

let allSamples = API.getData('replicatesAverage');
let selectedSamples = API.getData('selectedSamples');
let samplesToDisplay = API.getData('samplesToDisplay');

if (this.action) {
    switch (this.action.name) {
        case 'activatedAction': {
            API.getData('activatedSample').triggerChange();
        }
        case 'showOnlyCurves':
            samplesToDisplay.data = [];
            for (let i = 0; i < selectedSamples.length; i++) {
                addSamples(selectedSamples[i])
            }
            break;
        case 'showCurves':
            for (let i = 0; i < selectedSamples.length; i++) {
                addSamples(selectedSamples[i])
            }
            break;
        case 'hideCurves':
            for (let i = 0; i < selectedSamples.length; i++) {
                removeSamples(selectedSamples[i])
            }
            break;
        case 'hideAllCurves':
            samplesToDisplay.data = [];
            break;

        case 'showAllCurves':
            for (let i = 0; i < allSamples.length; i++) {
                addSamples(allSamples[i])
            }
            break;
        case 'cleanDisplayer': 
            samplesToDisplay.data = []
            API.createData('wellClicked', []);
            API.createData('clickedRow', [])
            break
        case 'cleanClickedWells':
            API.createData('wellClicked', []);
            break
        case 'cleanClickedRow':
            API.createData('clickedRow', []);
            break
    }
}

function addSamples(sample){
    let highlight = sample._highlight;
    let exist = samplesToDisplay.data
        .find((element) => element._highlight === highlight);
    if (exist) return;
    samplesToDisplay.data.push(sample);
}

function removeSamples(sample) {
    let highlight = sample._highlight;
    let index = samplesToDisplay.data
        .findIndex((element) => element._highlight === highlight);
    samplesToDisplay.data.splice(index, index + 1)
}

samplesToDisplay.triggerChange();
let displayCurves = samplesToDisplay.data
    .map((sample) => sample.growthCurve);
API.createData('displayCurves', {
    data: displayCurves
})

const currentAnalysisResults=API.getData('currentAnalysisResults');

const userAnalysisResults=API.cache("userAnalysisResults");
const templates=API.getData('analysisTemplates');
switch (this.action.name) {
    case 'refreshResults':
        await userAnalysisResults.refresh();
        break;
    case 'deleteResultTemplate':
    case 'deleteResult':
        await userAnalysisResults.deleteTemplate(this.action.value)
        API.doAction('refreshResults');
        break;

    case 'saveResultTemplate': {
        const value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        const name=await UI.enterValue({
            label: 'Enter the template name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;
        let result = await userAnalysisResults.saveTemplate(name, {
            kind: "template"
        }, {
            preferences: API.getData('preferences').resurrect(),
            protocolInfo: API.getData('protocolInfo').resurrect(),
            experimentSetup: API.getData('experimentSetup').resurrect(),
            wellsTemplate: API.getData('wellsTemplate').resurrect(),
        });
        API.doAction('refreshResults');
        break;
    }

    case 'saveExperiment': {
        const value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        const name=await UI.enterValue({
            label: 'Enter the experiment name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;
        let result = await userAnalysisResults.saveTemplate(name, {
            kind: 'experiment'
        }, {
            preferences: API.getData('preferences').resurrect(),
            protocolInfo: API.getData('protocolInfo').resurrect(),
            experimentSetup: API.getData('experimentSetup').resurrect(),
            wellsExperiment: API.getData('wellsExperiment').resurrect(),
        });
        API.doAction('refreshResults');
        break
    }

    case 'loadResultTemplate': {
        const result = await userAnalysisResults.loadResult(this.action.value);
        switch(DataObject.resurrect(this.action.value.$content.kind)) {
            case 'experiment': {
                API.createData('preferences', result.preferences)
                API.createData('protocolInfo', result.protocolInfo);
                // API.createData('experimentSetup', result.experimentSetup);
                API.createData('experimentData', result.experimentSetup);
                API.createData('wellsExperiment', result.wellsExperiment);
                API.createData('labelsList', result.wellsExperiment[0]
                    .experiment.map((item) => item.userID));
                API.createData('experimentInfo', {
                    experimentName: this.action.value.$id[3],
                    experimentSetup: result.experimentSetup,
                })
                API.doAction('editPreferences');
                break
            }
            case 'template': {
                API.createData('preferences', result.preferences)
                API.createData('protocolInfo', result.protocolInfo);
                API.createData('wellsTemplate', result.wellsTemplate);
                API.createData('labelsList', result.wellsTemplate[0]
                    .experiment.map((item) => item.userID));
                API.createData('templateInfo', {
                    templateName: this.action.value.$id[3],
                })
                API.doAction('editPreferences');
                break
            }
        }
        break;
    }

    case 'reloadProtocol':
        if (! templates) {
            UI.showNotification('Only available when linked to a database','warning');
            return;
        }
        let protocol = await UI.form(`
            <div style="zoom: 1.5;">
                <form>
                    <p style="padding-bottom: 10px">
                    Reload one of the previously saved preferences:
                    </p>
                    <select name='template'>
                        ${templates.map( template => {
                            let key=String(template.$id[3]);
                            let _id=String(template._id);
                            return `
                                <option value="${_id}">${key}</option>
                            `
                        })}

                    </select>
                    <input type="submit" value="Load preferences"/>
                </form>
            </div>
        `)
        if (protocol) {
          let result = await userAnalysisResults.loadResult({_id: protocol.template});
          API.createData('protocolInfo', result.protocolInfo);
          API.doAction('editPreferences');
        }
        break
    default:
}

