                const rows = experimentSetup.rows;
                const cols = experimentSetup.columns;
                const labels = API.getData('labelsList') != null ?
                    API.getData('labelsList').resurrect(): defaultLabels;

                let entries = Object.entries({
                    rows: rows,
                    cols: cols,
                });

                for (let i = 0; i < entries.length; i++) {
                    if (Number.isNaN(parseInt(entries[i][1]))) {
                        const label = entries[i][1].toUpperCase().charCodeAt(0);
                        const axis = new Array(label - 64)
                        .fill()
                        .map((item, index) => String.fromCharCode(index + 65));
                    entries[i][1] = axis;
                    } else {
                        const axis = new Array(parseInt(entries[i][1]))
                        .fill()
                        .map((item, index) => index + 1);
                        entries[i][1] = axis;
                    }
                }

                list = [
                    ['row', 'column'].concat(labels)
                ];

                list[0][list[0].length-1] = `${list[0][list[0].length-1]}${'\n'}`

                for (let i = 0; i < entries[0][1].length; i++) {
                    for (let j = 0; j < entries[1][1].length; j++) {
                        let row = new Array (2 + labels.length).fill(0)
                        row[0] = `${entries[0][1][i]}`;
                        row[1] = `${entries[1][1][j]}`;
                        row[row.length-1] = `${row[row.length-1]}${'\n'}`;
