async function onMessage(data) {
    if(data.type === 'tab.data') {
        const couchDB = data.message.couchDB;
        const uuid = data.message.uuid;
        API.cache('couchDB', couchDB);
        API.cache('uuid', uuid);
        const roc = new Roc(couchDB);
        API.cache('roc', roc);
        const userInfo = await roc.getUserInfo();
        API.cache('userInfo', userInfo)
        const userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
        API.cache("userAnalysisResults", userAnalysisResults);
        userAnalysisResults.refresh();

        // available until create the view on the couch to build the toc
        await roc.query('sample_toc', {varName: "samples"}).then(function(output) {
            let extract = [];
            let stock = [];
            for (let item of output) {
                if (item.value.metadata.kind === 'fraction' || item.value.metadata.kind === 'extract') extract.push(extracts(item))
                if (item.value.metadata.kind === 'stock') stock.push(extracts(item))
            }
            API.createData('extracts', extract);
            API.createData('stock', stock);
        })
    }
}

function extracts(element) {
    return {
        reference: element.key[1].split(" ")[0],
        batch: element.key[1].split(" ")[1],
        uuid: element.id,
        owner: element.key[0]
    }
}

