var action=getAction() || {name:''};
const roc = API.cache('roc')
let sample=API.cache('sample');
if (!sample) return;
let uuid=sample.uuid;
let id=sample.sample.$id;
if(this.action) {
    switch (this.action.name) {
        case 'save':
            sample.sample.$content.samplePreparation.protocol = API.getData('protocol')
        default:
            sample.handleAction(this.action);
    }
} else if (this.variable) {
    switch (this.variable) {
        case 'droppedData':
            sample.handleDrop(this.variable, true, {
                autoJcamp: true
            });
            break;
        case 'droppedVideo':
        case 'droppedImage':
        case 'droppedOther':
            console.log(this.variable)
            await sample.handleDrop(this.variable);
            break;
        case 'droppedOverview':
            sample.handleOverview(this.variable);
            break;
    }
}

let links = API.getData('videoLinks').resurrect();
let dbUrl = API.getData('dbUrl');
let videoURL;

switch (links.display) {
    case 'database': {
        if (dbUrl === null) return;
        if (dbUrl) {
            videoURL = dbUrl.resurrect();
        } else {
            UI.showNotification('Please choose one of video list', 'error')
        }
        break
    }
    case 'youtube': {
        let url = links.youtube;
        if (url.length !== 0) {
            videoURL = `https://www.youtube.com/embed/${getId(url)}?rel=0`
        } else {
            UI.showNotification('There is no YOUTUBE link to play', 'error')
        }
        break
    }
    case 'drive': {
        let url = links.drive;
        if (url.length !== 0) {
            videoURL = url.replace('view', 'preview');
        } else {
            UI.showNotification('There is no Google drive link to play', 'error')
        }
        break
    }
    case 'false': {
        videoURL = '';
        break
    }
};

API.createData('url', videoURL);

function getId(url) {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11)
      ? match[2]
      : null;
}

