        tiles: tilesData,
        shouldRender: tile => {
            if(tile.hide) return false;
            let user = API.cache('user');
            if(user) user = user.username;
            return (checkRights(user, tile.rights, true));
        },
        ribbon: tile => {
            if(tile.rights) {
                return 'LIMITED';
            } else if (tile.date) {
                const d = new Date(tile.date);
                if(new Date() - d < 1296000000) {
                    return 'NEW';
                }
            }
            return '';
        },
        isActive: tile => {
            const sample = API.getData('sampleClicked');
            if (tile.kind === 'sample'){
                switch (sampleKind) {
                    case 'sample':
                        if (sample) {
                            return true;
                        } else {
                            return false;
                        }
                        break

                    case 'extract':
                        let extract = tile.id === 'Extracts' || tile.id === 'gclc MS' || tile.id === 'Fractions' ? true : false;
                        if (sample && extract) {
                            return true;
                        } else {
                            return false;
                        }
                        break

                    case 'fraction':
                        let fraction = tile.id === 'Fractions' || tile.id === 'gclc MS' ? true : false;
                        if (sample && fraction) {
                            return true;
                        } else {
                            return false;
                        }
                        break
                }
            } else {
                return true
            }
