        icon: tile => tile.icon,
        title: tile => tile.icon ? '' : checkCache(tile.title),
        backgroundColor: tile => String(tile.bgcolor),
        color: tile => tile.fontcolor || 'black',
        header: tile => tile.description,
        footer: tile => tile.id,
        onTileClick: (event, tile) => {
            if(event.shiftKey && tile.viewURL) {
                return showCode(tile);
            }
            handleTileClick(tile);
        },
        isNewTabLink: (tile) => tile.kind === 'newTab',
        href: (tile) => window.location.origin + tile.viewURL
    });
}

async function showCode(tile) {
    const copiedTile = Object.assign({}, tile);
    delete copiedTile.line;

    const res = await superagent.get(couchPublicUrl + '/' + couchPublicDatabase + '/' + tile.viewURL)
    .set('Accept', 'application/json');
    if(res.status === 200) {
        copiedTile.rev = res.body._rev;
    } else {
        throw new Error('This view might not be public (' + res.status + ')');
    }

    const roc = new Roc({
        url: 'https://gat.mylims.org/',
        database: 'visualizer'
    });

    let title = '';
    try {
        const view = await roc.get(tile.viewURL);
        if(res.body._rev === view._rev) {
            title = 'Private and public revs match';
        } else {
            title = `Private: rev ${revNumber(view._rev)}, Pubilc: rev ${revNumber(res.body._rev)}`;
        }
    } catch(e) {
        title = 'not able to get private revision of the view';
    }

    try {
        await UI.showCode({
            mode: 'json',
            content: JSON.stringify(copiedTile, null, '\t'),
            title: title
        });    
    } catch(e) {
        UI.showNotification(e.message, 'error');
    }
}

function handleTileClick(tile) {
    var sample = API.getData('sampleClicked');
    API.doAction('handleTileClick', {
        sample: {
            uuid: sample && sample.id,
            reference: sample && sample.key.slice(1).join(' ')
        },
        tile
    })
}

function checkCache(val) {
    if(val) {
        const parts = val.split(':');
        if(parts[0] === 'cache') {
            const jpath = parts[1].split('.');
            const name = jpath.splice(0, 1);
            if(jpath.length === 0) return API.cache(name[0]);
            return DataObject.resurrect(DataObject.check(Object.assign({}, API.cache(name[0]))).getChildSync(jpath));
        } else {
            return val;
        }
    }
}

function revNumber(rev) {
    const m = /^(\d+)/.exec(rev);
    return m && m[1];
}

