function handleTileClick(value={}) {
    let sample = DataObject.resurrect(value).sample;
    let tile = DataObject.resurrect(value).tile;
    tile.data = "";
    var uuid = sample.uuid;
    var reference = sample.reference;
    var referer=document.location.href.replace(/(\/\/.*?\/).*/,'$1').replace('blob:','');
    var viewURL=tile.viewURL;
    viewURL+='?referer='+encodeURIComponent(referer);
    if (tile.rev) viewURL+='&rev='+tile.rev;
    switch (String(tile.kind)) {
        case 'sample':
            if (! uuid || ! reference) return;
            IframeBridge.postMessage('tab.open', {
                id: `${tile.id} ${reference}`,
                url: 'https://gat.mylims.org/roc/db/visualizer/entry/' + tile.viewURL +'/view.json',
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined,
                    reference
                }
            });
            break;
        case 'optionalSample':
            uuid = tile.viewURL || '';
            IframeBridge.postMessage('tab.open', {
                id: tile.id,
                url: 'https://gat.mylims.org/roc/db/visualizer/entry/' + uuid +'/view.json',
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined
                }
            });
            break;
        case 'rawIframe':
            IframeBridge.postMessage('tab.open', {
                id: tile.id,
                url: tile.viewURL,
                rawIframe: true,
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined,
                }
            });
        case 'newTab':
            break;
        default:
            console.warn(`unknown tile kind: ${tile.kind}`);
            break;
    }
}

